mapper.select:
select id, exam_id, student_id, dept_id, class_info, total_score, theory_score, tech_score, status, submit_time
from biz_county_exam_student
where exam_id = #{examId} and student_id = #{studentId}

select id, exam_id, student_id, dept_id, class_info, total_score, theory_score, tech_score, status, submit_time
from biz_county_exam_student
where exam_id = #{examId}

mapper.update:
update biz_county_exam_student
total_score = #{totalScore}, theory_score = #{theoryScore}, tech_score = #{techScore}, status = #{status}, submit_time = #{submitTime},
where id = #{id}
